property.value:
my @fieldsToProperties = (
	{
	id      => q{instanceId},
	pattern => q{([^:=]+)(\s+)?(:|=)(\s+)?(.+)},
	action  => q{setProperty("$[propertyRoot]$1","$5")},
	},
);
push @::gMatchers, @fieldsToProperties;

step.command:
if ($[output] == 1) {
# Output to File
my $filePath = "$[/javascript 
if (myJobStep.fileDir.length != 0) {
  myJobStep.fileDir.replace(/\\/g,'/')+'/';
}
]$[fileName]";
open (FILE, ">$filePath") or die "Cannot open $filePath";
print FILE <<EOF;
$[Template]
EOF
close FILE;
}

if ($[output] == 0) {
# Output to step log
print <<EOF;
$[template]
EOF
}

$[/javascript 
  if(/root/.test(myJob.parameterToTest.toLowerCase())) {
       var action='echo Illegal attempt to include root && exit 1';
  } else {
       var action='echo Valid parameter input';
  }
action;
]
